sys_ui_action.script:
function compare() {
	var gaCompare = new GlideAjax('x_snc_compare_tool.CompareAJAX');
	gaCompare.addParam('sysparm_name', 'compareAction');
	gaCompare.addParam('sysparm_sys_id', g_form.getUniqueValue());
	gaCompare.addParam('sysparm_number', g_form.getValue('number'));
	gaCompare.addParam('sysparm_type', g_form.getValue('type'));
	gaCompare.addParam('sysparm_output_format',  g_form.getValue('output_format'));
	gaCompare.addParam('sysparm_output_filter',  g_form.getValue('output_filter'));
	gaCompare.addParam('sysparm_record_left', g_form.getValue('record_left'));
	gaCompare.addParam('sysparm_record_right', g_form.getValue('record_right'));
	gaCompare.addParam('sysparm_key_type', g_form.getValue('key_type'));
	gaCompare.addParam('sysparm_key_fields', g_form.getValue('key_fields'));
	gaCompare.addParam('sysparm_output_fields', g_form.getValue('output_fields'));
	gaCompare.addParam('sysparm_table_left', g_form.getValue('table_left'));
	gaCompare.addParam('sysparm_table_right', g_form.getValue('table_right'));
	gaCompare.addParam('sysparm_connection_alias_left', g_form.getValue('connection_alias_left'));
	gaCompare.addParam('sysparm_connection_alias_right', g_form.getValue('connection_alias_right'));
	gaCompare.addParam('sysparm_filter_condition', g_form.getValue('filter_condition'));
	gaCompare.addParam('sysparm_status', g_form.getValue('status'));
	if ((g_form.getValue('type').indexOf('one') == -1) && (g_form.getValue('output_format') == 'attach_csv')) {
		gaCompare.getXMLAnswer(showCompareAttach);
	} else {
		gaCompare.getXMLAnswer(showCompareScreen);
	} // function compare
} // function compare

function showCompareScreen(sReturn) {
	if (!sReturn) {
		g_form.addErrorMessage('no return');
		return;
	}
	var oReturn = sReturn.evalJSON();
	if (oReturn['bValid'] == true) {
		var dialog = new GlideBox({
			body: oReturn['sHTML'],
			title: oReturn['sTitle'],
			height: '95%',
			width: '95%',
			autoDimensionOnLoad: true
		}); // glidebox
		oReturn['sHTML'].evalScripts(true);
		dialog.render();
	} else {
		if (oReturn.refresh == true) {
			location.reload();
		} // if refresh
		g_form.addErrorMessage(oReturn['sMessage']);
	} // if valid
} // function showCompareScreen

function showCompareAttach(sReturn) {
	var oReturn = sReturn.evalJSON();
	if (oReturn['bValid'] == true) {
		location.reload();
	} else {
		if (oReturn['bRefresh'] == true) {
			location.reload();
		} // if refresh
		g_form.addInfoMessage(oReturn['sMessage']);
	}
} // function showCompareAttach
